/*animações*/
@keyframes girar{
    from{
        transform:rotate(0deg);
    }
    to{
        transform:rotate(360deg);
    }
}

@keyframes aumentarTamanho{
    from{
        width: 0px;
        height: 0px;
    }
    to{
        width: 175px;
        height: 85px;
    }
}

@keyframes piscar{
    0%{
        filter:brightness(100%);
    }
    25%{
        filter:brightness(250%);
    }
    50%{
        filter:brightness(100%);
    }
    75%{
        filter:brightness(250%);
    }
    100%{
        filter:brightness(100%);
    }
}

@keyframes fadein{
   to{ 
        opacity: 1; 
    } 

}

@keyframes slideInLeft {
    0% {
        transform: translateX(-20%);
        filter: drop-shadow(8px 5px 0px white);
    }
    25%{
        filter: drop-shadow(10px 8px 0px black);
    }
    50%{
        filter: drop-shadow(10px 8px 0px black);
    }
    75%{
        filter: drop-shadow(10px 8px 0px black);
    }
    100% {
        transform: translateX(0);
    }
}

@keyframes slideinout {
    0% {
      top:-50px;
    }
    25% {
      top:0;
    }
    50% {
      top:0;
    }
    75% {
      top:0;
    }
    100%{
      top:-50px;
    }
}

